import React, { useState, useEffect } from "react";
import { Row, Col, Button, Typography } from "antd";
import { useRouter } from "next/router";
import { useSelector, useDispatch } from "react-redux";
import { FacebookProvider, Share } from "react-facebook";
import storage from "../back/cloudStorage";

import { ADD_URL } from "../reducers/answer";
import { MAKE_DIRECT_TRUE } from "../reducers/answer";

import {
	halfCol,
	fullCol,
	centerCol,
	WidthImg,
	WidthDiv,
	Texty,
	Paragraphy,
	ActionBtn,
} from "../assets/css/style_result";
import { Spinner } from "react-activity";
import result from "../assets/result.json";

const Result = ({ name }) => {
	const v = result[name];
	const dispatch = useDispatch();
	const router = useRouter();

	const { url } = useSelector((state) => state.answer);
	const { isDirect } = useSelector((state) => state.answer);

	const [screenshotUrl, setScreenshotUrl] = useState(null);
	const [thumbUrl, setThumbUrl] = useState(null);
	const [goodUrl, setGoodUrl] = useState(null);
	const [badUrl, setBadUrl] = useState(null);

	useEffect(() => {
		setScreenshotUrl(url["screenshotUrl"]);
		setThumbUrl(url["thumbUrl"]);
		setGoodUrl(url["goodUrl"]);
		setBadUrl(url["badUrl"]);
	}, [url]);

	useEffect(() => {
		if (isDirect && !thumbUrl) {
			storage
				.ref()
				.child(v["screenshot"])
				.getDownloadURL()
				.then((url) => {
					setScreenshotUrl(url);
				})
				.catch((err) => {
					return;
				});

			storage
				.ref()
				.child(v["thumbnail"])
				.getDownloadURL()
				.then((url) => {
					setThumbUrl(url);
				})
				.catch((err) => {
					return;
				});

			storage
				.ref()
				.child(v["good_image"])
				.getDownloadURL()
				.then((url) => {
					setGoodUrl(url);
				})
				.catch((err) => {
					return;
				});

			storage
				.ref()
				.child(v["bad_image"])
				.getDownloadURL()
				.then((url) => {
					setBadUrl(url);
				})
				.catch((err) => {
					return;
				});
		} else {
			dispatch({
				type: MAKE_DIRECT_TRUE,
			});
		}
	}, [isDirect]);

	useEffect(() => {
		if (isDirect && screenshotUrl && thumbUrl && goodUrl && badUrl) {
			dispatch({
				type: ADD_URL,
				data: {
					screenshotUrl: screenshotUrl,
					thumbUrl: thumbUrl,
					goodUrl: goodUrl,
					badUrl: badUrl,
				},
			});
		}
	}, [screenshotUrl, thumbUrl, goodUrl, badUrl]);

	const toTop = () => {
		router.push("/");
	};

	const toKakao = () => {
		try {
			Kakao.Link.sendDefault({
				objectType: "feed",
				content: {
					title: "나와 찰떡궁합인 반려동물",
					description: v["title"],
					imageUrl: thumbUrl,
					link: {
						mobileWebUrl: "https://mbti-bf18c.web.app/",
						webUrl: "https://mbti-bf18c.web.app/",
					},
				},
				buttons: [
					{
						title: "나도 해볼래요",
						link: {
							mobileWebUrl: "https://mbti-bf18c.web.app/",
							webUrl: "https://mbti-bf18c.web.app/",
						},
					},
				],
			});
		} catch (err) {
			// console.log(err);
		}
	};

	useEffect(() => {
		try {
			Kakao.init("a5394119fa694f305316c59293958f9d");
		} catch (err) {
			// console.log(err);
		}
	}, []);

	return (
		<>
			<Row justify="center">
				<Texty top={30} font={15}>
					나와 찰떡궁합인 반려동물은?
				</Texty>
			</Row>
			<Row justify="center">
				<Texty strong top={10} font={30} color={"#be2edd"}>
					{v["title"]}
				</Texty>
			</Row>
			<Row justify="center">
				<Col {...centerCol}>
					{thumbUrl ? (
						<WidthImg src={thumbUrl} />
					) : (
						<>
							<WidthDiv>
								<Spinner />
							</WidthDiv>
						</>
					)}
				</Col>
			</Row>

			<Row justify="center">
				<Col {...fullCol}>
					<Texty font={15}>
						현재 100명 중 {v["competition"]}명이 경합중입니다!
					</Texty>
				</Col>
			</Row>

			<Row justify="center">
				<Texty strong top={20} font={20} color={"#be2edd"}>
					{v["subtitle"]}
				</Texty>
			</Row>

			<Row justify="center">
				<Col {...fullCol}>
					<Paragraphy top={20} font={15}>
						{v["description"]}
					</Paragraphy>
				</Col>
			</Row>

			<Row style={{ height: "40px" }}></Row>
			<Row justify="center">
				<Col {...halfCol}>
					<Row>
						<Button
							type="link"
							style={{ height: "100%" }}
							justify="center"
						>
							<WidthImg src={goodUrl} />
							<Texty font={15}>{v["good"]}</Texty>
						</Button>
					</Row>
					<Row justify="center">
						<Texty font={15}>함께하면 좋은 반려동물</Texty>
					</Row>
				</Col>
				<Col xs={3} sm={3} md={3} lg={3} xl={3}></Col>
				<Col {...halfCol}>
					<Row>
						<Button
							type="link"
							style={{ height: "100%" }}
							justify="center"
						>
