import React, { useState, useEffect } from "react";
import { Row, Col, Button, Typography } from "antd";
import { useRouter } from "next/router";
const { Text, Paragraph } = Typography;
import storage from "../back/cloudStorage";
import { FacebookProvider, Share } from "react-facebook";

import {
	halfCol,
	fullCol,
	centerCol,
	WidthImg,
	Texty,
	ActionBtn,
} from "../assets/css/style_result";
import Feature from "./Feature";
import result from "../assets/result.json";

const Result = ({ name }) => {
	const [v, setV] = useState(null);
	const [imgUrl, setImgUrl] = useState(null);
	const [thumbUrl, setThumbUrl] = useState(null);
	const router = useRouter();

	const toTop = () => {
		router.push("/");
	};

	const toKakao = () => {
		try {
			Kakao.Link.sendDefault({
				objectType: "feed",
				content: {
					title: "나와 찰떡궁합인 반려동물",
					description: v["title"],
					imageUrl: thumbUrl,
					link: {
						mobileWebUrl: "https://mbti-bf18c.web.app/",
						webUrl: "https://mbti-bf18c.web.app/",
					},
				},
				buttons: [
					{
						title: "나도 해볼래요",
						link: {
							mobileWebUrl: "https://mbti-bf18c.web.app/",
							webUrl: "https://mbti-bf18c.web.app/",
						},
					},
				],
			});
		} catch (err) {
			console.log(err);
		}
	};

	useEffect(() => {
		setV(result[name]);
		storage
			.ref()
			.child(v["image"])
			.getDownloadURL()
			.then((url) => {
				console.log("image url: ", url);
				setImgUrl(url);
			})
			.catch((err) => {
				console.log("image error: ", err);
				return;
			});

		storage
			.ref()
			.child(v["thumbnail"])
			.getDownloadURL()
			.then((url) => {
				console.log("thumb url: ", url);
				setThumbUrl(url);
			})
			.catch((err) => {
				console.log("thumb error: ", err);
				return;
			});

		try {
			Kakao.init("a5394119fa694f305316c59293958f9d");
		} catch (err) {
			console.log(err);
		}

		console.log("imgUrl : ", imgUrl);
		console.log("v[image] : ", v["image"]);
		console.log("thumbUrl : ", thumbUrl);
		console.log("v[thumbnail] : ", v["thumbnail"]);
		console.log(`thumb from local: ../assets/images/${v["id"]}.png`);
	}, []);

	return (
		<>
			<Row justify="center">
				<Texty top={50} font={15}>
					나와 찰떡궁합인 반려동물은?
				</Texty>
			</Row>
			<Row justify="center">
				<Texty strong top={15} font={30} color={"#be2edd"}>
					{v["title"]}
				</Texty>
			</Row>
			<Row justify="center">
				<Texty strong top={5} font={15}>
					{v["subtitle"]}
				</Texty>
			</Row>
			<Row justify="center">
				<Col {...centerCol}>
					{v ? (
						<WidthImg
							src={require(`../assets/images/${v["id"]}.png`)}
						/>
					) : (
						<></>
					)}
				</Col>
			</Row>
			<Feature title={v["feature"]["i1"]} value={v["feature"]["v1"]} />
			<Feature title={v["feature"]["i2"]} value={v["feature"]["v2"]} />
			<Feature title={v["feature"]["i3"]} value={v["feature"]["v3"]} />
			<Feature title={v["feature"]["i4"]} value={v["feature"]["v4"]} />
			<Feature
				title={v["feature"]["i5"]}
				value={v["feature"]["v5"]}
				color={"#eb4d4b"}
			/>
			<Row justify="center">
				<Col {...fullCol}>
					<Paragraph
						strong
						style={{
							textAlign: "center",
							display: "block",
							marginTop: "45px",
							marginBottom: "45px",
							fontSize: "15px",
						}}
					>
						{v["description"]}
					</Paragraph>
				</Col>
			</Row>
			<Row justify="center">
				<Col {...halfCol}>
					<Row>
						<Button
							type="link"
							style={{ height: "100%" }}
							justify="center"
						>
							<WidthImg
								src={`/assets/images/${v["good_image"]}.png`}
							/>
							{v["good"]}
						</Button>
					</Row>
					<Row justify="center">
						<Text>환상의 케미</Text>
					</Row>
				</Col>
				<Col xs={3} sm={3} md={3} lg={3} xl={3}></Col>
				<Col {...halfCol}>
					<Row>
						<Button
							type="link"
							style={{ height: "100%" }}
							justify="center"
						>
							<WidthImg
								src={`assets/images/${v["bad_image"]}.png`}
							/>
							{v["bad"]}
						</Button>
					</Row>
					<Row justify="center">
						<Text>환장의 케미</Text>
					</Row>
				</Col>
			</Row>

			<Row justify="center">
				<Col {...fullCol}>
					<ActionBtn onClick={toTop}>
						<Texty font={15}>처음부터 다시 해보기!</Texty>
					</ActionBtn>
				</Col>
			</Row>

			<Row justify="center">
				<Col xs={7} sm={7} md={4} lg={4} xl={4}>
					<ActionBtn>
						<a href={imgUrl} download>
							<Texty font={15}>이미지 저장</Texty>
						</a>
					</ActionBtn>
				</Col>
				<Col xs={1} sm={1} md={1} lg={1} xl={1}></Col>
				<Col xs={7} sm={7} md={4} lg={4} xl={4}>
					<ActionBtn
						onClick={toKakao}
						style={{ backgroundColor: "#FEE500" }}
					>
						<Texty strong font={15} color={"#000000"}>
							카카오 공유
						</Texty>
					</ActionBtn>
				</Col>
				<Col xs={1} sm={1} md={1} lg={1} xl={1}></Col>
				<Col xs={7} sm={7} md={4} lg={4} xl={4}>
					<FacebookProvider appId="3621305641226627">
						<Share href="http://www.facebook.com">
							{({ handleClick }) => (
								<ActionBtn
									onClick={handleClick}
									style={{
										backgroundColor: "#4267B2",
									}}
								>
									<Texty font={15} color={"#FFFFFF"}>
										페이스북 공유
									</Texty>
								</ActionBtn>
							)}
						</Share>
					</FacebookProvider>
				</Col>
			</Row>

			<Row style={{ height: "400px" }}></Row>
		</>
	);
};

export default Result;
