import React, { useRef, useEffect, useState } from "react";
import { Row, Col, Button } from "antd";
import list from "../assets/list.json";
import { OptionBtn, optionCol } from "../assets/css/style_test";
import { Texty } from "../assets/css/style_result";

const Question = ({ index, handler }) => {
	const q = list.arr;

	const click = (ans) => {
		if (document) {
			const list = document.getElementsByClassName("optionBtn");
			list[ans - 1].blur();
		}
		handler(ans);
	};

	return (
		<>
			<Row justify="center" align="middle">
				<Col {...optionCol}>
					<Texty top={100} font={20}>
						{q[index - 1].id}번
					</Texty>
				</Col>
			</Row>

			<Row justify="center" align="middle">
				<Col {...optionCol}>
					<Texty top={20} font={30}>
						{q[index - 1].q}
					</Texty>
				</Col>
			</Row>

			<Row style={{ height: "50px" }}></Row>

			<Row justify="space-around">
				<Col {...optionCol}>
					<OptionBtn
						className="optionBtn"
						block
						onClick={() => click(1)}
					>
						<Texty font={18}>{q[index - 1].a1}</Texty>
					</OptionBtn>
				</Col>
			</Row>
			<Row justify="space-around">
				<Col {...optionCol}>
					<OptionBtn
						className="optionBtn"
						block
						style={{ marginTop: "20px" }}
						onClick={() => click(2)}
					>
						<Texty font={18}>{q[index - 1].a2}</Texty>
					</OptionBtn>
				</Col>
			</Row>

			{q[index - 1].a3 ? (
				<>
					<Row justify="space-around">
						<Col {...optionCol}>
							<OptionBtn
								className="optionBtn"
								block
								style={{ marginTop: "20px" }}
								onClick={() => click(3)}
							>
								<Texty font={18}>{q[index - 1].a3}</Texty>
							</OptionBtn>
						</Col>
					</Row>
				</>
			) : (
				<></>
			)}
			{q[index - 1].a4 ? (
				<>
					<Row justify="space-around">
						<Col {...optionCol}>
							<OptionBtn
								className="optionBtn"
								block
								style={{ marginTop: "20px" }}
								onClick={() => click(4)}
							>
								<Texty font={18}>{q[index - 1].a4}</Texty>
							</OptionBtn>
						</Col>
					</Row>
				</>
			) : (
				<></>
			)}
		</>
	);
};

export default Question;
