import React, { useState, useEffect } from "react";
import { Col } from "antd";
import { useSelector, useDispatch } from "react-redux";
import { useRouter } from "next/router";
import storage from "../back/cloudStorage";
import { ADD_URL } from "../reducers/answer";
import { MAKE_DIRECT_TRUE } from "../reducers/answer";
import { CLEAR_URLS } from "../reducers/answer";
import {
	RowsContainer,
	GoodBadContainer,
	WidthImg,
	Texty,
	Paragraphy,
	LineBreakText,
	GoodBadBtn,
} from "../assets/css/style_result";
import { Container, containerCol } from "../assets/css/style_global";

import result from "../assets/result.json";
import Footer from "./Footer";
import ShareGroup from "./ShareGroup";
import icons from "../back/icon_result";

const Result = ({ name }) => {
	const v = result[name];
	const dispatch = useDispatch();
	const router = useRouter();

	const { url } = useSelector((state) => state.answer);
	const { isDirect } = useSelector((state) => state.answer);

	const [thumbUrl, setThumbUrl] = useState(null);
	const [goodTitle, setGoodTitle] = useState("");
	const [goodId, setGoodId] = useState("");
	const [badTitle, setBadTitle] = useState("");
	const [badId, setBadId] = useState("");

	useEffect(() => {
		setThumbUrl(url["thumbUrl"]);

		setGoodTitle(result[v["good"]]["title"]);
		setGoodId(result[v["good"]]["id"]);
		setBadTitle(result[v["bad"]]["title"]);
		setBadId(result[v["bad"]]["id"]);
	}, [url]);

	useEffect(() => {
		dispatch({ type: CLEAR_URLS });
		if (isDirect && !thumbUrl) {
			console.log("[isDirect]");

			storage
				.ref()
				.child(v["thumbnail"])
				.getDownloadURL()
				.then((url) => {
					setThumbUrl(url);
				})
				.catch((err) => {
					return;
				});
		} else {
			dispatch({
				type: MAKE_DIRECT_TRUE,
			});
		}
	}, [isDirect]);

	useEffect(() => {
		if (isDirect && thumbUrl) {
			dispatch({
				type: ADD_URL,
				data: {
					thumbUrl: thumbUrl,
				},
			});
		}
	}, [thumbUrl]);

	const toGoodBad = (id) => {
		if (id === "") {
			return;
		}
		router.push("/result/".concat(id));
		dispatch({ type: CLEAR_URLS });
	};

	return (
		<>
			<Container top={-1}>
				<Col {...containerCol}>
					<WidthImg
						src={require("../assets/images/result/header.png")}
					/>

					<Texty strong top={10} font={30}>
						{v["title_1"]}
					</Texty>
					<Texty strong top={-1} font={30}>
						{v["title_2"]}
					</Texty>

					<WidthImg
						src={icons[v["id"]]}
						style={{ width: "90%", transform: "translateX(5%)" }}
					/>

					<Texty strong top={10} font={20}>
						{v["subtitle"]}
					</Texty>

					<Paragraphy top={10} font={15}>
						{v["description"]}
					</Paragraphy>

					<WidthImg
						src={require("../assets/images/result/line_horizontal.png")}
						style={{ marginTop: "20px", marginBottom: "20px" }}
					/>

					<RowsContainer>
						<GoodBadContainer>
							<Texty font={15} strong>
								잘 맞는 친구
							</Texty>

							<GoodBadBtn onClick={() => toGoodBad(goodId)}>
								<LineBreakText font={15}>
									{goodTitle}
								</LineBreakText>
								<WidthImg src={icons[goodId]} />
							</GoodBadBtn>
						</GoodBadContainer>

						<GoodBadContainer width={4}>
							<img
								src={require("../assets/images/result/line_vertical.png")}
								style={{ height: "200px" }}
							/>
						</GoodBadContainer>

						<GoodBadContainer>
							<Texty font={15} strong>
								어색한 친구
							</Texty>

							<GoodBadBtn onClick={() => toGoodBad(badId)}>
								<LineBreakText font={15}>
									{badTitle}
								</LineBreakText>
								<WidthImg src={icons[badId]} />
							</GoodBadBtn>
						</GoodBadContainer>
					</RowsContainer>

					<div style={{ height: "20px" }}></div>

					<ShareGroup
						title={v["title"] ? v["title"] : ""}
						thumbUrl={thumbUrl ? thumbUrl : ""}
					/>

					<div style={{ height: "50px" }}></div>

					<Footer />
				</Col>
			</Container>
		</>
	);
};

export default Result;
