import React, { useRef, useEffect, useState } from "react";
import { Row, Col, Button } from "antd";
import list from "../assets/list.json";
import {
	OptionsContainer,
	FullBtn,
	HalfBtn,
	optionCol,
	halfCol,
} from "../assets/css/style_test";
import { Texty } from "../assets/css/style_result";

const Question = ({ index, handler }) => {
	const q = list.arr;

	const click = (ans) => {
		if (document) {
			const list = document.getElementsByClassName("btn");
			list[ans - 1].blur();
		}
		handler(ans);
	};

	const font = 25;

	return (
		<>
			<div>
				<Texty font={font}>Q. {q[index - 1].id}</Texty>
			</div>

			<div>
				<Texty top={20} font={font}>
					{q[index - 1].q}
				</Texty>
			</div>

			<div style={{ height: "50px" }}></div>

			{q[index - 1].a3 ? (
				<>
					<Row justify="space-around">
						<Col {...optionCol}>
							<FullBtn className="btn" onClick={() => click(1)}>
								{q[index - 1].a1}
							</FullBtn>
						</Col>
					</Row>
					<Row justify="space-around">
						<Col {...optionCol}>
							<FullBtn className="btn" onClick={() => click(2)}>
								{q[index - 1].a2}
							</FullBtn>
						</Col>
					</Row>
				</>
			) : (
				<>
					<OptionsContainer>
						<HalfBtn className="btn" onClick={() => click(1)}>
							{q[index - 1].a1}
						</HalfBtn>

						<HalfBtn className="btn" onClick={() => click(2)}>
							{q[index - 1].a2}
						</HalfBtn>
					</OptionsContainer>
				</>
			)}

			{q[index - 1].a3 ? (
				<>
					<Row justify="space-around">
						<Col {...optionCol}>
							<FullBtn
								className="btn"
								block
								style={{ marginTop: "20px" }}
								onClick={() => click(3)}
							>
								<Texty font={font}>{q[index - 1].a3}</Texty>
							</FullBtn>
						</Col>
					</Row>
				</>
			) : (
				<></>
			)}
			{q[index - 1].a4 ? (
				<>
					<Row justify="space-around">
						<Col {...optionCol}>
							<FullBtn
								className="btn"
								block
								style={{ marginTop: "20px" }}
								onClick={() => click(4)}
							>
								<Texty font={font}>{q[index - 1].a4}</Texty>
							</FullBtn>
						</Col>
					</Row>
				</>
			) : (
				<></>
			)}
		</>
	);
};

export default Question;

