import React, { useEffect } from "react";
import { useDispatch } from "react-redux";
import { FacebookProvider, Share } from "react-facebook";
import { Row, Col } from "antd";
import { Texty, ActionBtn, fullCol } from "../assets/css/style_result";
import { CLEAR_URLS } from "../reducers/answer";

function ShareGroup({ title, thumbUrl }) {
	const dispatch = useDispatch();

	const toKakao = () => {
		try {
			Kakao.Link.sendDefault({
				objectType: "feed",
				content: {
					title: "나와 찰떡궁합인 반려동물",
					description: title,
					imageUrl: thumbUrl,
					link: {
						mobileWebUrl: "https://mbti-bf18c.web.app/",
						webUrl: "https://mbti-bf18c.web.app/",
					},
				},
				buttons: [
					{
						title: "나도 해볼래요",
						link: {
							mobileWebUrl: "https://mbti-bf18c.web.app/",
							webUrl: "https://mbti-bf18c.web.app/",
						},
					},
				],
			});
		} catch (err) {
			// console.log(err);
		}
	};

	useEffect(() => {
		try {
			Kakao.init("a5394119fa694f305316c59293958f9d");
		} catch (err) {
			// console.log(err);
		}
	}, []);

	const toTop = () => {
		dispatch({ type: CLEAR_URLS });
		router.push("/");
	};

	const toRandom = () => {
		dispatch({ type: CLEAR_URLS });
		const types = [
			"alpaca",
			"chihuahua",
			"cow",
			"crab",
			"deoksun",
			"dinosaur",
			"fish",
			"giraffe",
			"gorilla",
			"hamster",
			"hedgehog",
			"husky",
			"koala",
			"llama",
			"malteeze",
			"meerkat",
			"panda",
			"parrot",
			"persian",
			"polar",
			"pomenarian",
			"poodle",
			"quokka",
			"rabbit",
			"retriever",
			"shorthair",
			"sloth",
			"squirrel",
			"turtle",
			"unicorn",
			"welsh",
			"woodpecker",
		];
		const randomType = types[1 + Math.floor(Math.random() * 32)];
		router.push("/result/".concat(randomType));
	};

	return (
		<>
			<Row justify="center">
				<Col xs={7} sm={7} md={4} lg={4} xl={4}>
					<ActionBtn onClick={toTop}>
						<Texty font={15}>다시 해보기!</Texty>
					</ActionBtn>
				</Col>
				<Col xs={1} sm={1} md={1} lg={1} xl={1}></Col>
				<Col xs={7} sm={7} md={4} lg={4} xl={4}>
					<ActionBtn
						onClick={toKakao}
						style={{ backgroundColor: "#FEE500" }}
					>
						<Texty strong font={15} color={"#000000"}>
							카카오톡 공유
						</Texty>
					</ActionBtn>
				</Col>
				<Col xs={1} sm={1} md={1} lg={1} xl={1}></Col>
				<Col xs={7} sm={7} md={4} lg={4} xl={4}>
					<FacebookProvider appId="3621305641226627">
						<Share href="http://www.facebook.com">
							{({ handleClick }) => (
								<ActionBtn
									onClick={handleClick}
									style={{
										backgroundColor: "#4267B2",
									}}
								>
									<Texty font={15} color={"#FFFFFF"}>
										페이스북 공유
									</Texty>
								</ActionBtn>
							)}
						</Share>
					</FacebookProvider>
				</Col>
			</Row>

			<Row justify="center">
				<Col {...fullCol}>
					<ActionBtn top={0} onClick={toRandom}>
						<Texty font={15}>결과값 셔플</Texty>
					</ActionBtn>
				</Col>
			</Row>
		</>
	);
}

export default ShareGroup;
