				<Carousel
					dotPosition="left"
					ref={carouselRef}
					dots="false"
					effect="scrollx"
					style={{ marginTop: "100px" }}
				>
					{arr.map((c) => {
						return <Question index={c.id} key={c.id} />;
						//return <h1>index={c.id}</h1>;
					})}
				</Carousel>
			</Row>
		</>
	);
};

export default Dog;
