import React, { useState, useEffect } from "react";
import { Row, Col, Button, Typography } from "antd";
import { useRouter } from "next/router";
const { Text, Paragraph } = Typography;
import storage from "../back/cloudStorage";
import { FacebookProvider, Share } from "react-facebook";

import {
	halfCol,
	fullCol,
	centerCol,
	WidthImg,
	Texty,
	ActionBtn,
} from "../assets/css/style_result";
import Feature from "./Feature";
import result from "../assets/result.json";

const Result = ({ name }) => {
	const v = result[name];
	const vid = `../assets/images/${v["id"]}.png`;
	console.log(vid);
	const vgood = `../assets/images/${v["good_image"]}.png`;
	const vbad = `../assets/images/${v["bad_image"]}.png`;

	const [imgUrl, setImgUrl] = useState(null);
	const [thumbUrl, setThumbUrl] = useState(null);
	const router = useRouter();

	const toTop = () => {
		router.push("/");
	};

	const toKakao = () => {
		try {
			Kakao.Link.sendDefault({
				objectType: "feed",
				content: {
					title: "나와 찰떡궁합인 반려동물",
					description: v["title"],
					imageUrl: thumbUrl,
					link: {
						mobileWebUrl: "https://mbti-bf18c.web.app/",
						webUrl: "https://mbti-bf18c.web.app/",
					},
				},
				buttons: [
					{
						title: "나도 해볼래요",
						link: {
							mobileWebUrl: "https://mbti-bf18c.web.app/",
							webUrl: "https://mbti-bf18c.web.app/",
						},
					},
				],
			});
		} catch (err) {
			console.log(err);
		}
	};

	useEffect(() => {
		storage
			.ref()
			.child(v["image"])
			.getDownloadURL()
			.then((url) => {
				console.log("image url: ", url);
				setImgUrl(url);
			})
			.catch((err) => {
				console.log("image error: ", err);
				return;
			});

		storage
			.ref()
			.child(v["thumbnail"])
			.getDownloadURL()
			.then((url) => {
				console.log("thumb url: ", url);
				setThumbUrl(url);
			})
			.catch((err) => {
				console.log("thumb error: ", err);
				return;
			});

		try {
			Kakao.init("a5394119fa694f305316c59293958f9d");
		} catch (err) {
			console.log(err);
		}

		console.log("imgUrl : ", imgUrl);
		console.log("v[image] : ", v["image"]);
		console.log("thumbUrl : ", thumbUrl);
		console.log("v[thumbnail] : ", v["thumbnail"]);
		console.log(`thumb from local: ../assets/images/${v["id"]}.png`);
	}, []);

	return <></>;
};

export default Result;
