import React, { useRef, useEffect, useState } from "react";
import { Carousel, Row } from "antd";
import { useRouter } from "next/router";
import { useDispatch } from "react-redux";

import { ADD_ANSWER } from "../reducers/answer";
import Question from "../components/Question";
import ProgressBar from "../components/ProgressBar";
import list from "../assets/list.json";

const Dog = () => {
	const router = useRouter();
	const dispatch = useDispatch();

	const carouselRef = useRef();

	const [count, setCount] = useState(0);
	const maxCount = list.arr.length;
	const [answerArr, setAnswerArr] = useState([]);

	function handler(ans, id) {
		if (count !== id) {
			setAnswerArr((prevArr) => [...prevArr, ans]);

			if (id === maxCount) {
				router.push("/loading");
				return;
			}
			carouselRef.current.next();
			setCount(id);
		} else {
			return;
		}
	}

	useEffect(() => {
		if (answerArr.length === maxCount) {
			console.log("AnswerArr is: " + answerArr);
			dispatch({
				type: ADD_ANSWER,
				data: answerArr,
			});
		}
	});

	return (
		<div>
			<Carousel
				dotPosition="left"
				dots="false"
				ref={carouselRef}
				effect="fade"
				style={{ marginTop: "100px" }}
			>
				<Question index={1} handler={handler} />
				<Question index={2} handler={handler} />
				<Question index={3} handler={handler} />
				<Question index={4} handler={handler} />
				<Question index={5} handler={handler} />
				<Question index={6} handler={handler} />
				<Question index={7} handler={handler} />
			</Carousel>

			<Row justify="center" align="middle">
				<Col xs={18} sm={18} md={16} lg={14} xl={12}>
					<ProgressBar
						i={count}
						length={maxCount}
						style={{ marginTop: "100px" }}
					/>
				</Col>
			</Row>
		</div>
	);
};

export default Dog;
