import React from "react";
import { Row, Col, Button } from "antd";
import list from "../assets/list.json";
import styled from "styled-components";

const Question = ({ index, handler }) => {
	const q = list.arr;

	return (
		<>
			<div className="index">
				<Row justify="center" align="middle">
					<h1>Q{q[index - 1].id}</h1>
				</Row>
			</div>
			<div className="title">
				<Row justify="center" align="middle">
					<h3>{q[index - 1].q}</h3>
				</Row>
			</div>
			<div className="options">
				<Row justify="space-around">
					<Col xs={16} sm={14} md={10} lg={8} xl={8}>
						<OptionBtn
							type="primary"
							block
							style={{ marginTop: "20px" }}
							onClick={() => handler(1, q[index - 1].id)}
						>
							{q[index - 1].a1}
						</OptionBtn>
					</Col>
				</Row>
				<Row justify="space-around">
					<Col xs={16} sm={14} md={10} lg={8} xl={8}>
						<OptionBtn
							type="primary"
							block
							style={{ marginTop: "10px" }}
							onClick={() => handler(2, q[index - 1].id)}
						>
							{q[index - 1].a2}
						</OptionBtn>
					</Col>
				</Row>
			</div>
		</>
	);
};

const OptionBtn = styled(Button)`
	background: #eeeeee;
	color: #000000;
	border: none;

	font-size: 15px;
	display: block;
	margintop: 10px;

	&:hover {
		background: #0984e3;
		color: #ffffff;
	}
`;

export default Question;
