import React, { useRef, useEffect, useState } from "react";
import { useRouter } from "next/router";
import { useDispatch } from "react-redux";
import Slider from "react-slick";

import { ADD_ANSWER } from "../reducers/answer";
import Question from "../components/Question";
import ProgressBar from "../components/ProgressBar";
import list from "../assets/list.json";

const Test = () => {
	const router = useRouter();
	const dispatch = useDispatch();

	const [count, setCount] = useState(0);
	const maxCount = list.arr.length;
	const [answerArr, setAnswerArr] = useState([]);

	const indexArr = [1, 2, 3, 4, 5, 6, 7];
	const slickRef = useRef();

	function handler(ans, id) {
		if (count !== id) {
			setAnswerArr((prevArr) => [...prevArr, ans]);

			if (id === maxCount) {
				router.push("/");
				return;
			}
			slickRef.current.slickNext();
			setCount(id);
		} else {
			console.log("too fast");
			return;
		}
	}

	useEffect(() => {
		if (answerArr.length === maxCount) {
			console.log("AnswerArr is: " + answerArr);
			dispatch({
				type: ADD_ANSWER,
				data: answerArr,
			});
		}
	});

	return (
		<div>
			<Slider
				infinite={false}
				swipe={false}
				draggable={false}
				style={{ marginTop: "100px", height: "100%" }}
				ref={slickRef}
				fade={true}
			>
				{indexArr.map((ind) => {
					return (
						<div>
							<Question index={ind} handler={handler} />
						</div>
					);
				})}
			</Slider>

			<ProgressBar i={count} length={maxCount} />
		</div>
	);
};

export default Test;
