import React, { useRef, useEffect, useState } from "react";
import { Row, Col, Button } from "antd";
import list from "../assets/list.json";
import {
	OptionBtn,
	SelectBtn,
	optionCol,
	halfCol,
} from "../assets/css/style_test";
import { Texty } from "../assets/css/style_result";

const Question = ({ index, handler }) => {
	const q = list.arr;

	const click = (ans) => {
		if (document) {
			const list = document.getElementsByClassName("btn");
			list[ans - 1].blur();
		}
		handler(ans);
	};

	const font = 25;

	return (
		<>
			<Row justify="center" align="middle">
				<Col {...optionCol}>
					<Texty top={100} font={font}>
						Q. {q[index - 1].id}
					</Texty>
				</Col>
			</Row>

			<Row justify="center" align="middle">
				<Col {...optionCol}>
					<Texty top={20} font={font}>
						{q[index - 1].q}
					</Texty>
				</Col>
			</Row>

			<div style={{ height: "50px" }}></div>

			<Row justify="space-around">
				<Col {...optionCol}>
					<SelectBtn className="btn" block onClick={() => click(1)}>
						<Texty font={font}>{q[index - 1].a1}</Texty>
					</SelectBtn>
				</Col>
			</Row>
			<Row justify="space-around">
				<Col {...optionCol}>
					<SelectBtn
						className="btn"
						block
						style={{ marginTop: "20px" }}
						onClick={() => click(2)}
					>
						<Texty font={font}>{q[index - 1].a2}</Texty>
					</SelectBtn>
				</Col>
			</Row>

			{q[index - 1].a3 ? (
				<>
					<Row justify="space-around">
						<Col {...optionCol}>
							<SelectBtn
								className="btn"
								block
								style={{ marginTop: "20px" }}
								onClick={() => click(3)}
							>
								<Texty font={font}>{q[index - 1].a3}</Texty>
							</SelectBtn>
						</Col>
					</Row>
				</>
			) : (
				<></>
			)}
			{q[index - 1].a4 ? (
				<>
					<Row justify="space-around">
						<Col {...optionCol}>
							<SelectBtn
								className="btn"
								block
								style={{ marginTop: "20px" }}
								onClick={() => click(4)}
							>
								<Texty font={font}>{q[index - 1].a4}</Texty>
							</SelectBtn>
						</Col>
					</Row>
				</>
			) : (
				<></>
			)}
		</>
	);
};

export default Question;
