import React, { useRef, useEffect, useState } from "react";
import { Row, Col, Button, Carousel } from "antd";
import list from "../assets/list.json";
import Question from "../components/Question";
import { render } from "react-dom";

const Dog = () => {
	const carouselRef = useRef();
	const arr = list.arr;
	const [answerArr, setAnswerArr] = useState([]);
	const [renderArr, setRenderArr] = useState([]);

	arr.forEach((v) => {
		setRenderArr((prevArr) => [
			...prevArr,
			<Question index={v.index} key={v.index} />,
		]);
	});

	const handler = (ans) => {
		carouselRef.current.next();
		//console.log(ans);
	};

	// const questionList = list.map((obj) => (
	// 	<Question index={obj.index} handler={handler} key={obj.q} />
	// ));

	const nextQuestionFunc = () => {
		carouselRef.current.next();
	};

	useEffect(() => {
		console.log("length is: ", arr.length);
		console.log("arr is: ", arr);
		if (renderArr.length === 4) {
			console.log("renderArr is : ", renderArr);
		}
	}, [renderArr]);

	return (
		<>
			<Row justify="center" style={{ marginBottom: "50px" }}>
				<Button onClick={nextQuestionFunc}>다음</Button>
			</Row>

			<Row justify="center">
				<Carousel dotPosition="left" ref={carouselRef}>
					{/* {renderArr} */}
				</Carousel>
			</Row>
		</>
	);
};

export default Dog;
