import React, { useRef, useEffect, useState } from "react";
import { Row, Col, Button } from "antd";
import list from "../assets/list.json";
import { RowsContainer, FullBtn, HalfBtn } from "../assets/css/style_test";
import { Texty } from "../assets/css/style_result";

const Question = ({ index, handler }) => {
	const q = list.arr;

	const click = (ans) => {
		if (document) {
			const list = document.getElementsByClassName("btn");
			list[ans - 1].blur();
		}
		handler(ans);
	};

	const font = 25;

	return (
		<>
			<div>
				<Texty font={font}>Q. {q[index - 1].id}</Texty>
			</div>

			<div>
				<Texty top={20} font={font}>
					{q[index - 1].q}
				</Texty>
			</div>

			<div style={{ height: "50px" }}></div>

			{q[index - 1].a3 ? (
				<>
					<FullBtn className="btn" onClick={() => click(1)}>
						{q[index - 1].a1}
					</FullBtn>

					<FullBtn className="btn" onClick={() => click(2)}>
						{q[index - 1].a2}
					</FullBtn>

					<FullBtn className="btn" onClick={() => click(3)}>
						{q[index - 1].a3}
					</FullBtn>
				</>
			) : (
				<>
					<RowsContainer>
						<HalfBtn className="btn" onClick={() => click(1)}>
							{q[index - 1].a1}
						</HalfBtn>

						<HalfBtn className="btn" onClick={() => click(2)}>
							{q[index - 1].a2}
						</HalfBtn>
					</RowsContainer>
				</>
			)}

			{q[index - 1].a4 ? (
				<>
					<FullBtn className="btn" onClick={() => click(4)}>
						{q[index - 1].a4}
					</FullBtn>
				</>
			) : (
				<></>
			)}
		</>
	);
};

export default Question;

