import React, { useRef, useEffect } from "react";
import { Row, Col, Button } from "antd";
import list from "../assets/list.json";
import { OptionBtn, optionCol } from "../assets/css/style_test";

const Question = ({ index, handler }) => {
	const q = list.arr;
	const ref1 = useRef();
	const ref2 = useRef();

	const onClick = (ans) => {
		handler(ans);
		// document.activeElement.blur();
		// switch (ans) {
		// 	case 1:
		// 		ref1.current.blur();
		// 	case 2:
		// 		ref2.current.blur();
		// 	default:
		// 		return;
		// }

		useEffect(() => {
			// document.activeElement.blur();

			switch (ans) {
				case 1:
					ref1.current.blur();
				case 2:
					ref2.current.blur();
				default:
					return;
			}
		}, []);
	};

	return (
		<>
			<div className="index">
				<Row justify="center" align="middle">
					<h1>{q[index - 1].id}번문제</h1>
				</Row>
			</div>
			{/* <div className="title">
				<Row justify="center" align="middle">
					<h3>{[index - 1].q}</h3>
				</Row>
			</div> */}
			<div className="options">
				<Row justify="space-around">
					<Col {...optionCol}>
						<OptionBtn
							block
							style={{ marginTop: "20px" }}
							ref={ref1}
							onClick={onClick(1)}
						>
							{q[index - 1].a1}
						</OptionBtn>
					</Col>
				</Row>
				<Row justify="space-around">
					<Col {...optionCol}>
						<OptionBtn
							block
							style={{ marginTop: "10px" }}
							ref={ref2}
							onClick={onClick(2)}
						>
							{q[index - 1].a2}
						</OptionBtn>
					</Col>
				</Row>
			</div>
		</>
	);
};

export default Question;
