import React, { useState, useEffect } from "react";
import { Row, Col } from "antd";
import { useSelector, useDispatch } from "react-redux";
import { useRouter } from "next/router";
import storage from "../back/cloudStorage";
import { ADD_URL } from "../reducers/answer";
import { MAKE_DIRECT_TRUE } from "../reducers/answer";
import { CLEAR_URLS } from "../reducers/answer";
import {
	halfCol,
	fullCol,
	centerCol,
	WidthImg,
	WidthDiv,
	Texty,
	Paragraphy,
	LineBreakText,
	GoodBadBtn,
} from "../assets/css/style_result";
import { Spinner } from "react-activity";
import result from "../assets/result.json";
import Footer from "./Footer";
import ShareGroup from "./ShareGroup";

const Result = ({ name }) => {
	const v = result[name];
	const dispatch = useDispatch();
	const router = useRouter();

	const { url } = useSelector((state) => state.answer);
	const { isDirect } = useSelector((state) => state.answer);

	const [screenshotUrl, setScreenshotUrl] = useState(null);
	const [thumbUrl, setThumbUrl] = useState(null);
	const [goodUrl, setGoodUrl] = useState(null);
	const [badUrl, setBadUrl] = useState(null);

	const goodObj = result[v["good"]] ? result[v["good"]] : "";
	const badObj = result[v["bad"]] ? result[v["bad"]] : "";

	useEffect(() => {
		setScreenshotUrl(url["screenshotUrl"]);
		setThumbUrl(url["thumbUrl"]);
		setGoodUrl(url["goodUrl"]);
		setBadUrl(url["badUrl"]);
	}, [url]);

	useEffect(() => {
		dispatch({ type: CLEAR_URLS });
		if (isDirect && !thumbUrl) {
			storage
				.ref()
				.child(v["screenshot"])
				.getDownloadURL()
				.then((url) => {
					setScreenshotUrl(url);
				})
				.catch((err) => {
					return;
				});

			storage
				.ref()
				.child(v["thumbnail"])
				.getDownloadURL()
				.then((url) => {
					setThumbUrl(url);
				})
				.catch((err) => {
					return;
				});

			storage
				.ref()
				.child(result[v["good"]]["thumbnail"])
				.getDownloadURL()
				.then((url) => {
					setGoodUrl(url);
				})
				.catch((err) => {
					return;
				});

			storage
				.ref()
				.child(result[v["bad"]]["thumbnail"])
				.getDownloadURL()
				.then((url) => {
					setBadUrl(url);
				})
				.catch((err) => {
					return;
				});
		} else {
			dispatch({
				type: MAKE_DIRECT_TRUE,
			});
		}
	}, [isDirect]);

	useEffect(() => {
		if (isDirect && screenshotUrl && thumbUrl && goodUrl && badUrl) {
			dispatch({
				type: ADD_URL,
				data: {
					screenshotUrl: screenshotUrl,
					thumbUrl: thumbUrl,
					goodUrl: goodUrl,
					badUrl: badUrl,
				},
			});
		}
	}, [screenshotUrl, thumbUrl, goodUrl, badUrl]);

	const toGoodBad = (id) => {
		dispatch({ type: CLEAR_URLS });
		try {
			router.push("/result/".concat(id));
		} catch (err) {
			console.log(err);
		}
	};

	return (
		<>
			<Row justify="center">
				<Texty top={30} font={15}>
					나와 찰떡궁합인 반려동물은?
				</Texty>
			</Row>
			<Row justify="center">
				<Col {...centerCol}>
					<Texty strong top={10} font={30} color={"#be2edd"}>
						{v["title"]}
					</Texty>
				</Col>
			</Row>
			<Row justify="center">
				<Col {...centerCol}>
					{thumbUrl ? (
						<WidthImg src={thumbUrl} />
					) : (
						<>
							<WidthDiv>
								<Spinner />
							</WidthDiv>
						</>
					)}
				</Col>
			</Row>

			<Row justify="center">
				<Col {...fullCol}>
					<Texty font={15}>
						현재 100명 중 {v["competition"]}명이 경합중입니다!
					</Texty>
				</Col>
			</Row>

			<Row justify="center">
				<Texty strong top={10} font={20} color={"#be2edd"}>
					{v["subtitle"]}
				</Texty>
			</Row>

			<Row justify="center">
				<Col {...fullCol}>
					<Paragraphy top={10} font={15}>
						{v["description"]}
					</Paragraphy>
				</Col>
			</Row>

			<Row style={{ height: "40px" }}></Row>
			<Row justify="center">
				<Col {...halfCol}>
					<Row justify="center">
						<Texty font={15} strong>
							~함께하면 좋은 반려동물~
						</Texty>
					</Row>
					<Row>
						<GoodBadBtn
							type="link"
							justify="center"
							onClick={toGoodBad(goodObj["id"])}
						>
							<WidthImg src={goodUrl} />
							<LineBreakText font={15}>
								{goodObj["title"]}
							</LineBreakText>
						</GoodBadBtn>
					</Row>
				</Col>
				<Col xs={1} sm={2} md={2} lg={2} xl={3}></Col>
				<Col {...halfCol}>
					<Row justify="center">
						<Texty font={15} strong>
							~잘 맞지 않는 반려동물~
						</Texty>
					</Row>
					<Row>
						<GoodBadBtn
							type="link"
							justify="center"
							onClick={toGoodBad(badObj["id"])}
						>
							<WidthImg src={badUrl} />
							<LineBreakText font={15}>
								{badObj["title"]}
							</LineBreakText>
						</GoodBadBtn>
					</Row>
				</Col>
			</Row>

			<ShareGroup
				title={v["title"] ? v["title"] : ""}
				thumbUrl={thumbUrl ? thumbUrl : ""}
			/>

			<Footer />
		</>
	);
};

export default Result;
