import styled from "styled-components";
import { Typography } from "antd";
const { Text } = Typography;

export const containerCol = { xs: 16, sm: 11, md: 9, lg: 7, xl: 8 };

export const Container = styled.div`
	width: 100%;
	height: 100vh;

	display: flex;
	flex-direction: column;
	align-items: center;
	margin-top: 100px;
`;

export const WidthImg = styled.img`
	display: block;
	margin: 0;
	max-width: 100%;
	min-width: 100%;
	height: auto;
`;

export const Texty = styled.p`
	@import url("https://fonts.googleapis.com/css2?family=Poor+Story&display=swap");
	font-family: "Poor Story", cursive;

	text-align: center;
	display: block;
	margin-top: ${(props) => (props.top ? props.top : 0)}px;
	margin-bottom: ${(props) => (props.bottom ? props.bottom : 0)}px;
	font-size: ${(props) => (props.font ? props.font : 0)}px;
	color: ${(props) => (props.color ? props.color : "#333333")};
`;

export const CaptionContainer = styled.div`
	position: absolute;
	top: 0;
	width: 100%;
	height: 100%;

	display: flex;
	align-items: center;
	justify-content: center;
	background-color: #f66a70;
	opacity: 0.5;
	z-index: 100;
`;
