import React, { useRef, useEffect, useState } from "react";
import { Row, Col, Button, Carousel } from "antd";
import Question from "../components/Question";

import { ADD_ANSWER } from "../reducers/answer";
import { useDispatch } from "react-redux";

const Dog = () => {
	const carouselRef = useRef();

	const router = useRouter();
	const dispatch = useDispatch();
	const carouselElement = useRef(null);

	const [count, setCount] = useState(0);
	const maxCount = 2;
	const [answerArr, setAnswerArr] = useState([]);

	function handler(ans) {
		setAnswerArr((prevArr) => [...prevArr, ans]);

		if (count === maxCount) {
			router.push("/");
			return;
		}
		carouselRef.current.next();
		setCount(count + 1);
	}

	useEffect(() => {
		if (answerArr.length === maxCount + 1) {
			console.log("AnswerArr is: " + answerArr);
			// dispatch({
			// 	type: ADD_ANSWER,
			// 	data: answerArr,
			// });
		}
	});

	return (
		<div>
			<Carousel
				dotPosition="left"
				dots="false"
				ref={carouselElement}
				effect="scrollx"
				style={{ marginTop: "100px" }}
			>
				<Question index={1} handler={handler} />
				<Question index={2} handler={handler} />
				<Question index={3} handler={handler} />
				<Question index={4} handler={handler} />
				<Question index={5} handler={handler} />
				<Question index={6} handler={handler} />
				<Question index={7} handler={handler} />
			</Carousel>

			<ProgressBar i={count} length={maxCount} />
		</div>
	);
};

export default Dog;
