import React, { useState, useEffect } from "react";
import { Row, Col, Button, Typography } from "antd";
import { useRouter } from "next/router";
const { Text, Paragraph } = Typography;
import storage from "../back/cloudStorage";
import { FacebookProvider, Share } from "react-facebook";

import {
	halfCol,
	fullCol,
	centerCol,
	WidthImg,
	Texty,
	ActionBtn,
} from "../assets/css/style_result";
import Feature from "./Feature";
import result from "../assets/result.json";

const Result = ({ name }) => {
	const [id, setId] = useState(name);
	const [imgUrl, setImgUrl] = useState(null);
	const [thumbUrl, setThumbUrl] = useState(null);
	const router = useRouter();

	const toTop = () => {
		router.push("/");
	};

	const toKakao = () => {
		try {
			Kakao.Link.sendDefault({
				objectType: "feed",
				content: {
					title: "나와 찰떡궁합인 반려동물",
					description: v.title,
					imageUrl: thumbUrl,
					link: {
						mobileWebUrl: "https://mbti-bf18c.web.app/",
						webUrl: "https://mbti-bf18c.web.app/",
					},
				},
				buttons: [
					{
						title: "나도 해볼래요",
						link: {
							mobileWebUrl: "https://mbti-bf18c.web.app/",
							webUrl: "https://mbti-bf18c.web.app/",
						},
					},
				],
			});
		} catch (err) {
			console.log(err);
		}
	};

	useEffect(() => {
		console.log(name);
		console.log(result);
		// storage
		// 	.ref()
		// 	.child(v.image)
		// 	.getDownloadURL()
		// 	.then((url) => {
		// 		setImgUrl(url);
		// 	})
		// 	.catch((err) => {
		// 		return;
		// 	});

		// storage
		// 	.ref()
		// 	.child(v.thumbnail)
		// 	.getDownloadURL()
		// 	.then((url) => {
		// 		setThumbUrl(url);
		// 	})
		// 	.catch((err) => {
		// 		return;
		// 	});

		try {
			Kakao.init("a5394119fa694f305316c59293958f9d");
		} catch (err) {
			console.log(err);
		}
	}, []);

	return (
		<>
			<Row justify="center">
				<Texty top={50} font={15}>
					나의 유형은
				</Texty>
			</Row>
			{/* <Row justify="center">
				<Texty strong top={5} font={25}>
					{v.title}
				</Texty>
			</Row>
			<Row justify="center">
				<Texty strong top={5} font={30} color={"#be2edd"}>
					{v.subtitle}
				</Texty>
			</Row> */}
			<Row justify="center">
				<Col {...centerCol}>
					<WidthImg src={require("../assets/images/dog.png")} />
				</Col>
			</Row>
			<Feature title={"애정표현력"} value={40} />
			<Feature title={"눈치력"} value={40} />
			<Feature title={"썸추진력"} value={60} />
			<Feature title={"19금력"} value={40} />
			<Feature title={"마이웨이력"} value={100} color={"#eb4d4b"} />
			<Row justify="center">
				<Col {...fullCol}>
					<Paragraph
						strong
						style={{
							textAlign: "center",
							display: "block",
							marginTop: "45px",
							marginBottom: "45px",
							fontSize: "15px",
						}}
					>
						당신은 이러이러한 사람이에요.
						~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
					</Paragraph>
				</Col>
			</Row>
			<Row justify="center">
				<Col {...halfCol}>
					<Row>
						<Button
							type="link"
							style={{ height: "100%" }}
							justify="center"
						>
							<WidthImg
								src={require("../assets/images/cat.png")}
							/>
							환상유형
						</Button>
					</Row>
					<Row justify="center">
						<Text>환상의 케미</Text>
					</Row>
				</Col>
				<Col xs={3} sm={3} md={3} lg={3} xl={3}></Col>
				<Col {...halfCol}>
					<Row>
						<Button
							type="link"
							style={{ height: "100%" }}
							justify="center"
						>
							<WidthImg
								src={require("../assets/images/cat.png")}
							/>
							환장유형
						</Button>
					</Row>
					<Row justify="center">
						<Text>환장의 케미</Text>
					</Row>
				</Col>
			</Row>

			<Row justify="center">
				<Col {...fullCol}>
					<ActionBtn onClick={toTop}>
						<Texty font={15}>처음부터 다시 해보기!</Texty>
					</ActionBtn>
				</Col>
			</Row>

			<Row justify="center">
				<Col xs={7} sm={7} md={4} lg={4} xl={4}>
					<ActionBtn>
						<a href={imgUrl} download>
							<Texty font={15}>이미지 저장</Texty>
						</a>
					</ActionBtn>
				</Col>
				<Col xs={1} sm={1} md={1} lg={1} xl={1}></Col>
				<Col xs={7} sm={7} md={4} lg={4} xl={4}>
					<ActionBtn
						onClick={toKakao}
						style={{ backgroundColor: "#FEE500" }}
					>
						<Texty strong font={15} color={"#000000"}>
							카카오 공유
						</Texty>
					</ActionBtn>
				</Col>
				<Col xs={1} sm={1} md={1} lg={1} xl={1}></Col>
				<Col xs={7} sm={7} md={4} lg={4} xl={4}>
					<FacebookProvider appId="3621305641226627">
						<Share href="http://www.facebook.com">
							{({ handleClick }) => (
								<ActionBtn
									onClick={handleClick}
									style={{
										backgroundColor: "#4267B2",
									}}
								>
									<Texty font={15} color={"#FFFFFF"}>
										페이스북 공유
									</Texty>
								</ActionBtn>
							)}
						</Share>
					</FacebookProvider>
				</Col>
			</Row>

			<Row style={{ height: "400px" }}></Row>
		</>
	);
};

export default Result;
