import React, { useRef, useEffect, useState } from "react";
import { Row, Col, Button } from "antd";
import list from "../assets/list.json";
import { OptionBtn, optionCol } from "../assets/css/style_test";
import { Texty } from "../assets/css/style_result";

const Question = ({ index, handler }) => {
	const q = list.arr;

	const click = (ans) => {
		if (document) {
			const list = document.getElementsByClassName("optionBtn");
			list[ans - 1].blur();
		}
		handler(ans);
	};

	return (
		<>
			<Row justify="center" align="middle">
				<Col {...optionCol}>
					<Texty font={20}>{q[index - 1].id}번문제</Texty>
				</Col>
			</Row>

			<Row justify="center" align="middle">
				<Col {...optionCol}>
					<Texty font={15}>{q[index - 1].q}</Texty>
				</Col>
			</Row>

			<Row justify="space-around">
				<Col {...optionCol}>
					<OptionBtn
						className="optionBtn"
						block
						style={{ marginTop: "20px" }}
						onClick={() => click(1)}
					>
						{q[index - 1].a1}
					</OptionBtn>
				</Col>
			</Row>
			<Row justify="space-around">
				<Col {...optionCol}>
					<OptionBtn
						className="optionBtn"
						block
						style={{ marginTop: "10px" }}
						onClick={() => click(2)}
					>
						{q[index - 1].a2}
					</OptionBtn>
				</Col>
			</Row>
			{q[index - 1].a3 ? (
				<>
					<Row justify="space-around">
						<Col {...optionCol}>
							<OptionBtn
								className="optionBtn"
								block
								style={{ marginTop: "10px" }}
								onClick={() => click(3)}
							>
								{q[index - 1].a3}
							</OptionBtn>
						</Col>
					</Row>
				</>
			) : (
				<></>
			)}
			{q[index - 1].a4 ? (
				<>
					<Row justify="space-around">
						<Col {...optionCol}>
							<OptionBtn
								className="optionBtn"
								block
								style={{ marginTop: "10px" }}
								onClick={() => click(4)}
							>
								{q[index - 1].a4}
							</OptionBtn>
						</Col>
					</Row>
				</>
			) : (
				<></>
			)}
		</>
	);
};

export default Question;
